
import utils
from Day import Day



class Day1(Day):



    def parse(self, data):
        return data

    def run1(self, parsed, test = False):

        if not test:
            parsed = self.parse(parsed)

        lev = 0
        for c in parsed:
            if c == "(":
                lev += 1
            else:
                lev -= 1
        return lev

    def run2(self, parsed, test=False):
        if not test:
            parsed = self.parse(parsed)

        lev = 0

        for cind in range(len(parsed)):
            if parsed[cind] == "(":
                lev += 1
            else:
                lev -= 1

            if lev < 0:
                return cind+1




